import React, { useState } from 'react';
import './Quiz.css';
import { data } from '../assets/data';

const Quiz = () => {
  const [index, setIndex] = useState(0); // Set initial index to 0
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState(null);

  const question = data[index];

  const checkAns = (ans) => {
    setSelectedAnswer(ans);
    setCorrectAnswer(question.ans);
  };

  const nextQuestion = () => {
    if (index < data.length - 1) {
      setIndex(index + 1);
      setSelectedAnswer(null);
      setCorrectAnswer(null);
    }
  };

  return (
    <div className='container'>
      <h1>Quiz App</h1>
      <hr />
      <h2>{index + 1}. {question.question}</h2>
      <ul>
        {[question.option1, question.option2, question.option3, question.option4].map((option, i) => {
          const ans = i + 1;
          let className = '';
          if (selectedAnswer !== null) {
            if (ans === correctAnswer) {
              className = 'correct';
            } else if (ans === selectedAnswer) {
              className = 'wrong';
            }
          }
          return (
            <li key={ans} className={className} onClick={() => { checkAns(ans) }}>
              {option}
            </li>
          );
        })}
      </ul>
      <button onClick={nextQuestion}>Next</button>
      <div className='index'>{index + 1} of {data.length} questions</div>
    </div>
  );
};

export default Quiz;
